	}
}

@function color($name, $weigth) {
	@return var(--clr-#{$name}-#{$weigth});
}

@function rem($value, $root-font: $base-font) {
	$result: calc($value / $root-font);
	@return $result * 1rem;
}

/**
* Hachure/stripes background
* Permet de créer des background hachuré en css
* @param {Color} - Couleur de l'hachure en px
* @param {Size} - Taille de l'hachure en px
**/

@mixin stripes-bg($bg-color, $color, $size) {
	background-color: $bg-color;
	background-image: linear-gradient(
		135deg,
		$color 10%,
		transparent 0,
		transparent 50%,
		$color 0,
		$color 60%,
		transparent 0,
		transparent
	);
	background-size: $size $size;
}

@mixin space($size) {
	display: grid;
	gap: rem($size);
}

@mixin space-x($size) {
	> * + * {
		margin-left: rem($size);
	}
}

@mixin space-x-reverse($size) {
	> * + * {
		margin-right: rem($size);
	}
}

@mixin space-y($size) {
	> * + * {
		margin-top: rem($size);
	}
}

@mixin space-y-reverse($size) {
	> * + * {
		margin-bottom: rem($size) !important;
	}
}

