sources.src/styles/components/tasks/task.compone:
.task {
	border-radius: rem(16);
	padding: rem(16) rem(24);
	background-color: $color-gray-200;
	@include space-y(24);
	position: relative;
}

.task-header {
	display: flex;
	@include space-x(24);
}

.task-checkbox {
	border-radius: rem(8);
	background-color: $color-gray-300;
	border: 1px solid $color-gray-400;
	height: rem(32);
	width: rem(32);
	display: inline-block;
	flex: none;
}

.task-content {
	@include space-y(8);
	flex-grow: 1;
}

.task-title {
	font-weight: 600;
}

.task-description {
}

.task-move {
	@include flex-center;
	padding: rem(8);
	height: rem(32);
	width: rem(32);
	border-radius: rem(8);
	background-color: $color-gray-300;
	color: $color-gray-500;
}

.task hr {
	opacity: 0.3;
}

.task-footer {
	@include flex-between;
}

.task-badges {
	@include space-x(8);
	display: flex;
}

.task-timer {
	color: $color-gray-500;
	font-weight: 500;
	@include text-sm;
}

sources.src/styles/components/tooltip.scss:
.tooltip {
	@include flex-center;
	border-radius: rem(8);
	padding: rem(4) rem(8);
	display: none;
	position: fixed;
	box-shadow: 0 rem(2) rem(4) rgba($color: #000000, $alpha: 0.16);
}
.tooltip-visible-white {
	display: flex;
	background-color: $color-gray-200;

	.tooltip-label {
		color: $color-black;
	}

	.tooltip-arrow {
		background-color: $color-gray-200;
	}
}

.tooltip-visible-black {
	display: flex;
	background-color: $color-gray-900;

	.tooltip-label {
		color: $color-white;
	}

	.tooltip-arrow {
		background-color: $color-gray-900;
	}
}

.tooltip-content {
	padding: rem(4) rem(8);
}

.tooltip-label {
	@include text-xs;
	font-weight: 600;
}

.tooltip-arrow {
	position: absolute;
	height: rem(12);
	width: rem(12);
	border-radius: rem(2);
	transform: translateY(-50%) rotate(45deg);
	box-shadow: 0 rem(2) rem(3) rgba($color: #000000, $alpha: 0.16);
	left: rem(-4);
	top: 50%;
}

sources.src/styles/components/documents/task-doc:
